'use strict';

const request = require('supertest');

const db = require('../db.js');
const app = require('../app');
const User = require('../models/user');
const bcrypt = require('bcrypt');
const { BCRYPT_WORK_FACTOR } = require('../config');

const { commonBeforeEach, commonAfterEach, commonAfterAll, u1Token, u2Token } = require('./_testCommon');

beforeEach(commonBeforeEach);
afterEach(commonAfterEach);
afterAll(commonAfterAll);

/************************************** GET /users/:username */

describe('GET /users/:username', function() {
	test('works for same user', async function() {
		const newUser = {
			username: 'u1',
			fullName: 'U1FN',
			email: 'test@test.com',
			profileImageURL:
				'data:image/png;base64,',
			bio: 'new Test stuff'
		};
		await User.register({
			...newUser,
			password: 'password'
		});
		const resp = await request(app).get(`/users/u1`).set('authorization', `Bearer ${u1Token}`);
		expect(resp.body).toEqual({
			user: {
				...newUser,
				posts: [],
				postLikes: [],
				commentLikes: [],
				following: [],
				followers: []
			}
		});
	});

	test('unauth for anon', async function() {
		const resp = await request(app).get(`/users/u1`);
		expect(resp.statusCode).toEqual(401);
	});

	test('not found if user not found', async function() {
		const resp = await request(app).get(`/users/nope`).set('authorization', `Bearer ${u2Token}`);
		expect(resp.statusCode).toEqual(404);
	});
});

/************************************** POST /users/create */

describe('POST /users/create', function() {
	test('works', async function() {
		await db.query(
			`
			INSERT INTO users (username, password, full_name, profile_image_url, email, bio)
			VALUES ('u1', $1, 'U1FN', './public/images/default-pic.png', 'u1@email.com', 'test_user 1 stuff')
			RETURNING username`,
			[ await bcrypt.hash('password1', BCRYPT_WORK_FACTOR) ]
		);
		const newPost = {
			username: 'u1',
			postURL:
				'https://instagram-clone-photo.s3.us-west-1.amazonaws.com/uploads/99d8cbd9-975f-450f-8b0e-fd57a9e74caf-activation2.jpg',
			caption: 'I drew this 🖼️',
			watermark: null,
			watermarkFont: null,
			filter: null
		};
		const resp = await request(app)
			.post(`/users/create`)
			.send({ ...newPost })
			.set('authorization', `Bearer ${u1Token}`);
		expect(resp.body).toEqual({
			post: {
				...newPost,
				createdAt: expect.any(String),
				postId: expect.any(Number)
			}
		});
	});
});

/************************************** GET /users/comments/:postId */

describe('GET /users/comments/:postId', function() {
	test('works', async function() {
		const testPostsCreatedAt = [];
		const testPostsPostIds = [];
		const testCommentIds = [];
		await db.query(
			`
        INSERT INTO users(username,
                          password,
                          full_name,
                          email,
                          bio)
        VALUES ('u1', $1, 'U1FN', 'u1@email.com', 'test_user 1 stuff')
        RETURNING username`,
			[ await bcrypt.hash('password1', BCRYPT_WORK_FACTOR) ]
		);

		// u1 will have a post, and make two comments on it.
		const resultsPosts = await db.query(
			`
		INSERT INTO posts(username, post_url, caption)
		VALUES ('u1', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTc14uK4IYYBB8TU2JnCEmxKMeGEbfw_flCuQ&usqp=CAU', 'a fake post by u1')
		RETURNING created_at, post_id`
		);
		testPostsCreatedAt.splice(0, 0, ...resultsPosts.rows.map((r) => r.created_at));
		testPostsPostIds.splice(0, 0, ...resultsPosts.rows.map((r) => r.post_id));

		const resultsComments = await db.query(
			`
		INSERT INTO comments(message, username, post_id)
		VALUES ('One more comment about the photo', 'u1', $1), ('Another comment', 'u1', $1)
		RETURNING comment_id`,
			[ testPostsPostIds[0] ]
		);
		testCommentIds.splice(0, 0, ...resultsComments.rows.map((r) => r.comment_id));

		const resp = await request(app)
			.get(`/users/comments/${testPostsPostIds[0]}`)
			.set('authorization', `Bearer ${u1Token}`);
		expect(resp.body).toEqual({
			comments: [
				{
					commentId: testCommentIds[0],
					parentId: null,
					message: 'One more comment about the photo',
					username: 'u1',
					createdAt: expect.any(String),
					numLikes: '0',
					children: []
				},
				{
					commentId: testCommentIds[1],
					parentId: null,
					message: 'Another comment',
					username: 'u1',
					createdAt: expect.any(String),
					numLikes: '0',
					children: []
				}
			]
		});
	});
});

